mapper.update:
update hotel set hotel_name=#{hotelName},
hotel_location1=#{hotelLocation1}, hotel_location2=#{hotelLocation2}
where hotel_id = #{hotelId}

update room_detail
set parking = #{parking}, wifi=#{wifi}, pet=#{pet}, pool=#{pool}, breakfast=#{breakfast}, 
single_room=#{singleRoom}, twin_room=#{twinRoom}, double_room=#{doubleRoom}, triple_room=#{tripleRoom}
where room_id=#{roomId} and hotel_id=#{hotelId}
